//         />
//         <ExpensesChart expenses={filteredExpenses}/>
//         <ExpensesList items={filteredExpenses}/>
//       </Card>
//     </div>
//   );
// };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        /></Card>
        <Card>
        <ExpensesChart expenses={filteredExpenses}/>
        <ExpensesList items={filteredExpenses}/>
      </Card>
    </div>
  );
};


export default Expenses;
